import de.thb.fim.pizzaPronto.datenobjekte.DessertVO;import de.thb.fim.pizzaPronto.datenobjekte.GerichtVO;import de.thb.fim.pizzaPronto.datenobjekte.PersonVO;import de.thb.fim.pizzaPronto.logik.Angestellter;import de.thb.fim.pizzaPronto.logik.Koch;import org.junit.BeforeClass;import org.junit.Test;import java.lang.reflect.Method;import java.lang.reflect.Modifier;import static org.junit.Assert.assertFalse;import static org.junit.Assert.assertTrue;/** * Die Vererbungshierarchien werden getestet. *  * Zum Testen werden spezielle Assert-Befehle und die Reflection-API eingesetzt <br> *  * @author Gabriele Schmidt * @version 1.0 11.05.2015 */public class JUnitTestVererbungshierachien {		private static Class<PersonVO> myPersonVOClass;	private static int modifiersPersonVO; 	private static Class<Angestellter> myAngestellterClass;	private static int modifiersAngestellter;	private static Angestellter koch;		private static Method arbeiten;	private static int modifiersArbeiten;		private static Class<GerichtVO> myGerichtVOClass;	private static int modifiersGerichtVO; 		private static  GerichtVO dessert;		private static Method getGerichtName;	private static int modifiersgetGerichtName;	private static Method getGerichtNummer;	private static int modifiersgetGerichtNummer;	private static Method getName;	private static int modifiersgetName;	private static Method getNummer;	private static int modifiersgetNummer;		@BeforeClass	public static void setUpBeforeClass() throws Exception {		myAngestellterClass = Angestellter.class;		modifiersAngestellter= myAngestellterClass.getModifiers();				myPersonVOClass = PersonVO.class;				modifiersPersonVO = myPersonVOClass.getModifiers();				koch = new Koch();				arbeiten = myAngestellterClass.getMethod( "arbeiten" );		modifiersArbeiten = arbeiten.getModifiers();				myGerichtVOClass = GerichtVO.class;		modifiersGerichtVO= myGerichtVOClass .getModifiers();				dessert = new DessertVO();				getGerichtName = myGerichtVOClass.getMethod( "getGerichtName" );		modifiersgetGerichtName = getGerichtName.getModifiers();				getGerichtNummer = myGerichtVOClass.getMethod( "getGerichtNummer" );		modifiersgetGerichtNummer = getGerichtNummer.getModifiers();				getName = myGerichtVOClass.getMethod( "getName" );		modifiersgetName = getName.getModifiers();				getNummer = myGerichtVOClass.getMethod( "getNummer" );		modifiersgetNummer = getNummer.getModifiers();	}		@Test	public void isabstractPersonVO() {		assertTrue("de.thb.fim.pizzaPronto.datenobjekte.PersonVO ist abstract", Modifier.isAbstract(modifiersPersonVO));	}		@Test	public void isabstractAngestellter() {		assertTrue("de.thb.fim.pizzaPronto.logik.Angestellter ist abstract", Modifier.isAbstract(modifiersAngestellter));	}		@Test	public void testAngestellterOberklassePersonVO() {		assertTrue("de.thb.fim.pizzaPronto.logik.Angestellter ist eine de.thb.fim.pizzaPronto.datenobjekte.PersonVO", myAngestellterClass.getSuperclass().toString().equals("class de.thb.fim.pizzaPronto.datenobjekte.PersonVO"));	}			@Test	public void isabstractArbeiten() {			assertTrue("Methode arbeiten ist abstract", Modifier.isAbstract(modifiersArbeiten));	}		@Test	public void testKochOberklassen() {		assertTrue("de.thb.fim.pizzaPronto.logik.Koch ist ein de.thb.fim.pizzaPronto.logik.Koch", koch instanceof Koch);		assertTrue("de.thb.fim.pizzaPronto.logik.Koch ist eine de.thb.fim.pizzaPronto.logik.Angestellter", koch instanceof Angestellter);		assertTrue("de.thb.fim.pizzaPronto.logik.Koch ist eine de.thb.fim.pizzaPronto.datenobjekte.PersonVO", koch instanceof PersonVO);	}		@Test	public void isabstractGerichtVO() {		assertTrue("de.thb.fim.pizzaPronto.datenobjekte.GerichtVO ist abstract", Modifier.isAbstract(modifiersGerichtVO));	}				@Test	public void isabstractGetGerichtName() {			assertTrue("Methode getGerichtName ist abstract", Modifier.isAbstract(modifiersgetGerichtName));	}		@Test	public void isabstractGetGerichtNummer() {			assertTrue("Methode getGerichtNummer ist abstract", Modifier.isAbstract(modifiersgetGerichtNummer));	}	@Test	public void isNotabstractGetName() {			assertFalse("Methode getName ist nicht abstract", Modifier.isAbstract(modifiersgetName));	}		@Test	public void isNotabstractGetNummer() {			assertFalse("Methode getNummer ist nicht abstract", Modifier.isAbstract(modifiersgetNummer));	}		@Test	public void testDessertVOOberklasse() {		assertTrue("de.thb.fim.pizzaPronto.datenobjekte.DessertVO ist ein de.thb.fim.pizzaPronto.datenobjekte.DessertVO", dessert instanceof DessertVO);		assertTrue("de.thb.fim.pizzaPronto.datenobjekte.DessertVO ist eine de.thb.fim.pizzaPronto.datenobjekte.GerichtVO", dessert instanceof GerichtVO);	}	}